mapper.select:
select m.* , c.name as categoryName from material m left outer join category c on d.category_id = c.id
and m.name like concat('%',#{name},'%')
and m.category_id = #{categoryId}
and m.status = #{status}
order by m.create_time desc

mapper.update:
update material
name = #{name}, category_id = #{categoryId}, image = #{image}, description = #{description}, status = #{status}, numbers = #{number}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from material


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc
